class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n=len(target)
        m=len(words)
        randomm={}
        for i,q in enumerate(words):
            randomm[q]=min(randomm.get(q,float('inf')),costs[i]);
        x=[float('inf')]* (n+1)
        x[0]=0
        for i in range(1,n+1):
            for q in randomm:
                if i>=len(q) and target[i-len(q):i]==q:
                    x[i]=min(x[i],x[i-len(q)]+randomm[q])

        return x[n] if x[n]!=float('inf') else -1
