struct TrieNode {
    std::unordered_map<char, TrieNode*> children;
    std::vector<int> wordIndices;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const std::string& word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordIndices.push_back(index);
    }
};


#include <vector>
#include <string>
#include <climits>
#include <unordered_map>
#include <algorithm>

class Solution {
public:
    int minimumCost(std::string target, std::vector<std::string>& words, std::vector<int>& costs) {
        int n = target.size();
        std::vector<long long> dp(n + 1, LLONG_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == LLONG_MAX) continue;  // If this part of the string is not reachable, skip it.
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (int index : node->wordIndices) {
                    int len = words[index].size();
                    dp[j + 1] = std::min(dp[j + 1], dp[i] + costs[index]);
                }
            }
        }

        return dp[n] == LLONG_MAX ? -1 : dp[n];
    }
};
