class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> words; // Stores pairs of (length, cost)
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->words.push_back({word.size(), cost});
    }
};

class Solution {
public:
    typedef long long ll;

    int solve(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<ll> dp(n + 1, LLONG_MAX);
        dp[0] = 0; 

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == LLONG_MAX) {
                continue; 
            }

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];

                for (const auto& [length, cost] : node->words) {
                    if (j - i + 1 == length) {
                        dp[i + length] = min(dp[i + length], dp[i] + cost);
                    }
                }
            }
        }

        return dp[n] == LLONG_MAX ? -1 : dp[n];
    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        return solve(target, words, costs);
    }
};
