class Solution {
    class Trie {
        Trie[] children = new Trie[26];
        int cost = Integer.MAX_VALUE;

        public Trie() {
        }

        public void add(String s, int c) {
            var cur = this;
            for (int i = 0; i < s.length(); i++) {
                if (cur.children[s.charAt(i) - 'a'] == null) {
                    cur.children[s.charAt(i) - 'a'] = new Trie();
                }
                cur = cur.children[s.charAt(i) - 'a'];
            }
            cur.cost = Math.min(cur.cost, c);
        }

        public List<int[]> get(String target, int start) {
            var res = new ArrayList<int[]>();
            var cur = this;
            int len = 0;
            for (int i = start; i < target.length(); i++) {
                if (cur.children[target.charAt(i) - 'a'] == null) {
                    return res;
                }
                cur = cur.children[target.charAt(i) - 'a'];
                len++;
                if (cur.cost != Integer.MAX_VALUE) {
                    res.add(new int[] { len, cur.cost });
                }
            }
            return res;
        }
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        var trie = new Trie();
        for (int i = 0; i < costs.length; i++) {
            trie.add(words[i], costs[i]);
        }

        var dp = new int[target.length() + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        for (int start = 0; start < target.length(); start++) {
            if (dp[start] == Integer.MAX_VALUE) {
                continue;
            }
            var ww = trie.get(target, start);
            for (var w : ww) {
                if (dp[start + w[0]] > dp[start] + w[1]) {
                    dp[start + w[0]] = dp[start] + w[1];
                }
            }
        }
        return dp[target.length()] == Integer.MAX_VALUE ? -1 : dp[target.length()];
    }
}
