class TrieNode {
public:
    vector<TrieNode*> children;
    vector<pair<int, int>> word_indices;

    TrieNode() : children(26, nullptr) {}
};

class Solution {
private:
    void insert(TrieNode* root, const string& word, int index, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children[c - 'a']) {
                node->children[c - 'a'] = new TrieNode();
            }
            node = node->children[c - 'a'];
        }
        node->word_indices.emplace_back(index, cost);
    }

public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        vector<long long> dp(n + 1, LLONG_MAX);
        dp[0] = 0;

        TrieNode* root = new TrieNode();
        for (int i = 0; i < words.size(); ++i) {
            insert(root, words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == LLONG_MAX) continue;

            TrieNode* node = root;
            for (int j = i; j < n; ++j) {
                if (!node->children[target[j] - 'a']) break;
                node = node->children[target[j] - 'a'];

                for (const auto& [index, cost] : node->word_indices) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + cost);
                }
            }
        }

        return dp[n] == LLONG_MAX ? -1 : dp[n];
    }
};
