class Solution {
public:
    class trie_node{
        public:
        int cost = 0;
        trie_node **children = new trie_node*[26]{};
    };

    int DP[50001]{};
    trie_node *Root = NULL;
    int Recursion(string &target ,int n){
        if(n==0)return 0;
        if(DP[n])return DP[n];
        else{
            int ans = -1;
            int m = n;
            trie_node *root = Root;
            while(m--){
                root = root->children[target[m]-'a'];
                if(!root)break;
                if(root->cost){
                    int a = Recursion(target,m);
                    if(a!=-1){
                        if(ans==-1) ans = a + root->cost;
                        else ans = min(ans,a+root->cost);
                    }
                }
            }
            DP[n] = ans;
            return ans;
        }
    }
    void Add(trie_node*root,string &s,int &cost,int m){
        if(m==0){
            if(root->cost) root->cost = min(root->cost ,cost);
            else root->cost = cost;
            return;
        }
        trie_node *&child = root->children[s[m-1]-'a'];
        if(!child){
            child = new trie_node;
        }
        Add(child,s,cost ,m-1);
    }
