    int minimumCost(string &target, vector<string>& words, vector<int>& costs) {
        Root = new trie_node;
        int i = 0;
        for(string &word:words){
            Add(Root,word,costs[i],word.size());
            // cout<<is_there(Root,word,word.size())<<' ';
            i++;
        }
        int n = target.size();
        int ans = Recursion(target,n);
        return ans;
    }
};
