#define ll long long
struct Node
{
    const static int N = 26; // change this according to ur need
    Node *child[N];
    char data;
    int end = 0;
    int cost=1e5;
    Node(char d)
    {
        data = d;
        for (int i = 0; i < N; i++)
        {
            child[i] = nullptr;
        }
    }
    Node *next(int b, char d)
    {
        if (child[b] == nullptr)
        {
            child[b] = new Node(d);
        }
        return child[b];
    }
};
class Trie
{
public:
    Node *root;
    Trie()
    {
        root = new Node('\0');
    }
    void insert(string s,int c)
    {
        Node *temp = root;
        for (auto it : s)
        {
            int x = it - 'a'; // change this according to ur need
            temp = temp->next(x, it);
        }
        temp->end++;
        temp->cost=min(temp->cost,c);
    }
};
class Solution {
public:
    int minimumCost(string s, vector<string>& w, vector<int>& c) {
        Trie *t = new Trie();
        int n=s.size();
        int m = w.size();
        for(int i=0;i<m;i++)
        {
            t->insert(w[i],c[i]);
        }
        // last = 0-> new start otherwise prev
        vector<ll>dp(n,-1);
        auto f=[&](int idx,auto &&f)->ll
        {
            if(idx>=n)
            {
                return 0;
            }
            if(dp[idx]!=-1)
            {
                return dp[idx];
            }
            ll ans =INT_MAX;
            Node *temp = t->root;
            for(int i=idx;i<n&&(temp!=nullptr);i++)
            {
                int x = s[i]-'a';
                if(temp->child[x]==nullptr)
                {
                    break;
                }
                temp=temp->child[x];
                if(temp->end)
                {
                    ll tempans = temp->cost+f(i+1,f);
                    ans = min(ans,tempans);
                }
            }
            return dp[idx]= ans;
        };
        int ans = f(0,f);
        if(ans==INT_MAX)
        {
            ans=-1;
        }
        return ans;
    }
};
