class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        dicts = defaultdict(int)
        for idx_word,idx_cost in zip(words,costs):
            if idx_word in dicts:
                dicts[idx_word] = min(idx_cost,dicts[idx_word])
            else:
                dicts[idx_word] = idx_cost

        n = len(target)
        m = len(words)

        # 初始化 dp 数组
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # 动态规划填充 dp 数组
        for i in range(n):
            if dp[i] == float('inf'):
                continue

            for key,val in dicts.items():
                word = key
                cost = val
                if target[i:i+len(word)] == word:
                    dp[i + len(word)] = min(dp[i + len(word)], dp[i] + cost)

        # 检查结果
        return dp[n] if dp[n] != float('inf') else -1
