#define ll long long
class Node{
    public:
    unordered_map<char, Node*> children;
    ll minCost;
    bool end = false;

    Node() {
        this->minCost = INT_MAX;
    }

};
class Trie {
    public:
    Node * root;
    Trie() {
        root = new Node();
    }
    void insert(string& word, ll cost) {
        Node * curr = root;
        for (char c : word) {
            if (!curr->children.count(c)) {
                curr->children[c] = new Node();
            }
            curr = curr->children[c];
        }
        curr->minCost = min(curr->minCost, cost);
    }


    bool search(string word) {
        Node *curr = root;
        for(auto it : word){
            if(!curr->children[it-'a']){
                return false;
            }
            curr = curr->children[it-'a'];
        }
        return (curr->end == true);
    }
    bool startsWith(string prefix) {
        Node *curr = root;
        for(auto it : prefix){
            if(!curr->children[it-'a']){
                return false;
            }
            curr = curr->children[it-'a'];
        }
        return true;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        if (n == 0) return 0;
        int len = words.size();
        Trie mytrie;
        for (int i = 0; i < len; i++) {
            mytrie.insert(words[i], costs[i]);
        }
        vector<long long> dp(n + 1, INT_MAX);
        dp[n] = 0;

        for (int i = n - 1; i >= 0; i--) {
            Node* node = mytrie.root;
            for (int j = i; j < n; j++) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->minCost < INT_MAX) {
                    dp[i] = min(dp[i], node->minCost + dp[j + 1]);
                }
            }
        }

        return dp[0] == INT_MAX ? -1 : dp[0];
    }
};
