from typing import List

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        s = len(words)
        h = len(target)
        flag=True
        atak = {}
        for i, w in enumerate(words):
            flag=True
            atak[w] = min(atak.get(w, float('inf')), costs[i])

        t=[float('inf')]*(h + 1)
        t[0] = 0

        for i in range(1,h+ 1):
            for l in atak:
                flag=True
                if i >= len(l) and target[i - len(l):i] == l:
                    flag=False
                    t[i] = min(t[i], t[i - len(l)] + atak[l])

        return t[h] if t[h] != float('inf') else -1
