#define ll long long
class Solution {
public:
    class Trie{
    public:
        Trie*links[26];
        bool flag = false;
        int cost=1e5;
        bool isAbsent(char ch){
            return links[ch-'a']==NULL;
        }
        void insert(Trie*node,char ch){
            links[ch-'a'] = node;
        }
        Trie*get(char ch){
            return links[ch-'a'];
        }
    };

     Trie*root = new Trie();

    pair<bool,int> solve(Trie*node,string& word,int i){
        if(i==word.length())return {node->flag,node->cost};
        pair<bool,int>res = {false,1e9};
        if(!node->isAbsent(word[i])){
            res = solve(node->get(word[i]),word,i+1);
        }
        return res;
    }

    pair<bool,int> search(string& word) {
        Trie*node = root;
        return solve(node,word,0);
    }

    void addWord(string& word, int cost) {
        Trie*node = root;
        for(int i=0;i<word.length();i++){
                if(node->isAbsent(word[i])){
                    node->insert(new Trie(),word[i]);
                }
                node = node->get(word[i]);
        }
        node->cost = min(node->cost,cost);
        node->flag = true;
    }

    vector<ll>dp;

    ll f(int i, string& target) {
        if (i == target.length()) return 0;
        if (dp[i] != -1) return dp[i];
        ll mini = 1e9;
        Trie* node = root;
        for (int j = i; j < target.length(); j++) {
            if (node->isAbsent(target[j])) break;
            node = node->get(target[j]);
            if (node->flag) {
                mini = min(mini, node->cost + f(j + 1, target));
            }
        }
        return dp[i] = mini;
    }
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = words.size();
        for(int i=0;i<n;i++){
            addWord(words[i],costs[i]);
        }
        dp.resize(target.size(),-1);
        int ans = f(0,target);
        if(ans>=1e9)return -1;
        return ans;
    }
};
