class Solution:
    def minimumCost(self, targ: str, word: List[str], cost: List[int]) -> int:
        t = len(targ)
        w = len(word)

        dic = {}
        [dic.__setitem__(wo, min(dic.get(wo, float('inf')), cost[i])) for i, wo in enumerate(word)]

        d = [float('inf')] * (t + 1)
        d[0] = 0

        [d.__setitem__(i, min(d[i], d[i - len(wo)] + dic[wo])) for i in range(1, t + 1) for wo in dic if i >= len(wo) and targ[i - len(wo):i] == wo]

        return d[t] if d[t] != float('inf') else -1
