class Solution 
{
    static class Node
    {
        Node[] child;
        boolean isEnd;
        int cost;
        Node()
        {
            child = new Node[26];
            isEnd = false;
            cost = Integer.MAX_VALUE;
        }
    }

    public int minimumCost(String target, String[] words, int[] costs) 
    {
        var root = new Node();
        for(int i=0;i<words.length;i++)
        {
            Node trav = root;
            String e = words[i];
            for(char c: e.toCharArray())
            {
                if(trav.child[c-'a']==null)
                {
                    trav.child[c-'a'] = new Node();
                }

                trav = trav.child[c-'a'];
            }

            trav.isEnd = true;
            trav.cost = Math.min(trav.cost, costs[i]);
        }

        //check if "ef" exists.
        var temp = root.child['e'-'a'];
        //System.out.println(temp.child['f'-'a'].cost);

        int n = target.length();
        var dp = new int[n];
        Arrays.fill(dp, Integer.MAX_VALUE);
        for(int i=n-1;i>=0;i--)
        {
            Node trav = root;
            int j = i;
            //System.out.println(i);

            while(j<n && trav!=null)
            {
                trav = trav.child[target.charAt(j)-'a'];
                if(trav !=null && trav.isEnd)
                {
                    //System.out.println("jj");
                    if(j+1<n && dp[j+1]!=Integer.MAX_VALUE)
                    {
                        dp[i] = Math.min(trav.cost + dp[j+1], dp[i]);
                    }

                    if(j+1==n)  dp[i] = Math.min(dp[i], trav.cost);

                }
                j++;
            }

            //System.out.println(dp[i]);
        }

        if(dp[0]!=Integer.MAX_VALUE) return dp[0];
        return -1;
    }
}
