class Solution {
public:
    vector<int> ar;
    vector<int> dp;
    int mx;

    struct Trie{
        char ch;
        vector<Trie*> ar;
        bool isEnd;
        int co = INT_MAX;

        Trie(char c)
        {
            ch = c;
            co = INT_MAX;
            isEnd = false;
            ar = vector<Trie*>(26, NULL);
        }
    };

    Trie *parent;

    void insert(Trie *root, string& str, int i, int ctr)
    {
        if(i>=str.size())
        {
            root->isEnd = true;
            root->co = min(root->co, ctr);
            return;
        }

        if(root->ar[str[i]-'a']==NULL)
            root->ar[str[i]-'a'] = new Trie(str[i]-'a');
        insert(root->ar[str[i]-'a'], str, i+1, ctr);
    }

    long rec(Trie *root, string &str, int ind)
    {
        if(root==NULL)
            return INT_MAX;

        if(ind<dp.size()&&root==parent&&dp[ind]!=-1)
            return dp[ind];

        if(ind>=str.size())
        {
            if(root->isEnd)
                return root->co;
            return INT_MAX;
        }


        long ans = INT_MAX;
        if(root->isEnd)
        {

            long tmp = root->co;
            tmp += rec(parent, str, ind);
            ans = min(ans, tmp);
        }

        ans = min(ans, rec(root->ar[str[ind]-'a'], str, ind+1));
        if(root==parent)
            dp[ind]=ans;
        return ans;
    }


    int minimumCost(string s, vector<string>& words, vector<int>& br) {
        dp.assign(s.size()+1, -1);

        Trie *root = new Trie(0);
        parent = NULL;
        mx = INT_MAX;

        for(int i=0;i<words.size();++i)
            if(words[i].size()<=s.size())
                insert(root, words[i], 0, br[i]);

        string cur;
        parent = root;
        ar = br;

        int st = 0;
        mx = rec(root, s, 0);
        return mx == INT_MAX ? -1 : mx;
    }
};
