class Solution {
    static class TrieNode {
        TrieNode[] children = new TrieNode[26];
        int cost = Integer.MAX_VALUE;
    }

    static class Trie {
        TrieNode root = new TrieNode();

        public void insert(String word, int cost) {
            TrieNode node = root;
            for (char c : word.toCharArray()) {
                int index = c - 'a';
                if (node.children[index] == null) {
                    node.children[index] = new TrieNode();
                }
                node = node.children[index];
            }
            node.cost = Math.min(node.cost, cost);
        }

        public TrieNode getRoot() {
            return root;
        }
    }
    public int minimumCost(String target, String[] words, int[] costs) {
         int n = target.length();
        Trie trie = new Trie();

        // Insert all words into the trie
        for (int i = 0; i < words.length; i++) {
            trie.insert(words[i], costs[i]);
        }

        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        // Iterate through each position in the target
        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            TrieNode node = trie.getRoot();
            for (int j = i; j < n; j++) {
                int index = target.charAt(j) - 'a';
                if (node.children[index] == null) break;
                node = node.children[index];
                if (node.cost != Integer.MAX_VALUE) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
