class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
       Trie trie = new Trie();
        for (int i = 0; i < words.length; i++) {
            trie.insert(words[i], costs[i]);
        }

        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue; // Skip impossible states

            TrieNode node = trie.root;
            for (int j = i; j < n; j++) {
                char c = target.charAt(j);
                if (!node.children.containsKey(c)) break;
                node = node.children.get(c);

                if (node.cost != 0) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }

    static class TrieNode {
        Map<Character, TrieNode> children = new HashMap<>();
        int cost;
    }

    static class Trie {
        TrieNode root = new TrieNode();

        void insert(String word, int cost) {
            TrieNode node = root;
            for (char c : word.toCharArray()) {
                node.children.computeIfAbsent(c, k -> new TrieNode());
                node = node.children.get(c);
            }
            node.cost = Math.min(node.cost == 0 ? Integer.MAX_VALUE : node.cost, cost);
        }
    }


}
