class Solution {
    struct node{
        int val;
        node* child[26];
        bool isend;
        node(){
            val=1e9;
            for(int i=0;i<26;i++){
                child[i]=nullptr;
            }
            isend=false;
        }
    };
    node* root;
    void insert(string s,int val){
        node* ptr=root;
        for(int i=0;i<s.size();i++){
            if (ptr->child[s[i]-'a']==nullptr){
                ptr->child[s[i]-'a']=(node*) new node();
            }
            ptr=ptr->child[s[i]-'a'];
        }
        ptr->isend=true;
        ptr->val=min(ptr->val,val);
    }
    void dp(vector<int> & cost, string& target, int i,int n){
        int ans=1e9;
        node* ptr=root;
        for(int j=i;j<n;j++){
            if (ptr->child[target[j]-'a']==nullptr){
                break;
            }
            ptr=ptr->child[target[j]-'a'];
            if (ptr->isend){
                int val=ptr->val;
                if (val>0){
                    if (cost[j+1]==1e9){
                        dp(cost,target,j+1,n);
                    }
                    if (cost[j+1]<1e9-1) ans=min(ans,val+cost[j+1]);
                }
            }
        }
        if (ans==1e9){
            cost[i]=1e9-1;
        }else{
            cost[i]=ans;
        }
    }
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n=target.size();
        vector<int> cost(n+1,1e9);
        cost[n]=0;
        root=(node*) new node();

        for(int i=0;i<words.size();i++){
            insert(words[i],costs[i]);
        }


        dp(cost,target,0,n);
        if (cost[0]>=1e9-1) return -1;
        return cost[0];
    }
};
