class Node
{
    vector<Node *> links;
    bool flag;
    int cost;

public:
    Node()
    {
        links.resize(26, nullptr);
        flag = false;
        cost = INT_MAX;
    }

    Node *getLink(char c)
    {
        return links[c - 'a'];
    }

    bool getFlag()
    {
        return flag;
    }

    int getCost()
    {
        return cost;
    }

    void setLink(char c)
    {
        links[c - 'a'] = new Node();
    }

    void setFlag()
    {
        flag = true;
    }

    void setCost(int val)
    {
        cost = val;
    }
};

class Trie
{
    Node *root;

public:
    Trie()
    {
        root = new Node();
    }

    void insert(string word, int cost)
    {
        Node *temp = root;
        for (char c : word)
        {
            if (temp->getLink(c) == nullptr)
                temp->setLink(c);
            temp = temp->getLink(c);
        }
        temp->setCost(min(temp->getCost(), cost));
    }

    void solve(string target, vector<int> &cache, int l)
    {
        for (int i = 0; i < l; ++i)
        {
            if (cache[i] == INT_MAX)
                continue;
            Node *temp = root;
            for (int j = i; j < l; ++j)
            {
                char c = target[j];
                if (temp->getLink(c) == nullptr)
                    break;
                temp = temp->getLink(c);
                if (temp->getCost() != INT_MAX)
                    cache[j + 1] = min(cache[j + 1], cache[i] + temp->getCost());
            }
        }
    }
};
class Solution
{
public:
    int minimumCost(string target, vector<string> &words, vector<int> &costs)
    {
        int l = target.length(), n = words.size();
        Trie t;
        for (int i = 0; i < n; ++i)
            t.insert(words[i], costs[i]);
        vector<int> cache(l + 1, INT_MAX);
        cache[0] = 0;
        t.solve(target, cache, l);
        if (cache[l] != INT_MAX)
            return cache[l];
        return -1;
    }
};
