class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)

    def find_suffixes(self, target, start):
        node = self.root
        suffixes = []
        for i in range(start, len(target)):
            if target[i] not in node.children:
                break
            node = node.children[target[i]]
            if node.cost != float('inf'):
                suffixes.append((i + 1, node.cost))
        return suffixes

class Solution:

    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        trie = Trie()
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        for i in range(n):
            if dp[i] == float('inf'):
                continue
            suffixes = trie.find_suffixes(target, i)
            for end, cost in suffixes:
                dp[end] = min(dp[end], dp[i] + cost)

        return dp[n] if dp[n] != float('inf') else -1
