class Solution {
public:
    struct TrieNode {
        unordered_map<char, TrieNode*> children;
        vector<pair<int, int>> wordEndings; 
    };

    class Trie {
    public:
        TrieNode* root;
        Trie() { root = new TrieNode(); }

        void insert(string& word, int index, int cost) {
            TrieNode* currentNode = root;
            for (char c : word) {
                if (!currentNode->children.count(c))
                    currentNode->children[c] = new TrieNode();
                currentNode = currentNode->children[c];
            }
            currentNode->wordEndings.push_back({index, cost});
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int targetLength = target.size();
        vector<int> dp(targetLength + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int start = 0; start < targetLength; ++start) {
            if (dp[start] == INT_MAX) continue;

            TrieNode* currentNode = trie.root;
            for (int end = start; end < targetLength; ++end) {
                if (!currentNode->children.count(target[end])) break;
                currentNode = currentNode->children[target[end]];
                for (auto& wordInfo : currentNode->wordEndings) {
                    int wordIndex = wordInfo.first;
                    int wordCost = wordInfo.second;
                    int wordLength = words[wordIndex].size();
                    if (start + wordLength <= targetLength) {
                        dp[start + wordLength] = min(dp[start + wordLength], dp[start] + wordCost);
                    }
                }
            }
        }

        return dp[targetLength] == INT_MAX ? -1 : dp[targetLength];
    }
};
