class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        mp = {}
        for word, cost in zip(words, costs):
            if word in mp:
                mp[word] = min(mp[word], cost)
            else:
                mp[word] = cost

        dp = [1000000000] * (n + 1)
        dp[0] = 0

        for i in range(1, n + 1):
            for word in mp:
                if i >= len(word):
                    if target[i - len(word) : i] == word:
                        dp[i] = min(dp[i], dp[i - len(word)] + mp[word])

        return dp[n] if dp[n] != 1000000000 else -1
