class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        cost_dict = {words[0]: costs[0]}
        for i in range(len(words)):
            if(words[i] in cost_dict):
                cost_dict[words[i]] = min(cost_dict[words[i]], costs[i])
            else:
                cost_dict[words[i]] = costs[i]

        starting_char = {}
        for x in cost_dict.keys():
            if(x[0] in starting_char):
                starting_char[x[0]].append(x)
            else:
                starting_char[x[0]] = [x]

        indices_w_cost = {0: 0}
        while(indices_w_cost):
            curr_ind = min(indices_w_cost.keys())
            curr_cost = indices_w_cost[curr_ind]
            if(curr_ind == len(target)):
                return indices_w_cost[curr_ind]
            if(target[curr_ind] in starting_char):
                for x in starting_char[target[curr_ind]]:
                    cost = cost_dict[x]
                    if(target[curr_ind:curr_ind+len(x)] == x):
                        if(curr_ind+len(x) in indices_w_cost):
                            indices_w_cost[curr_ind+len(x)] = min(indices_w_cost[curr_ind+len(x)], curr_cost+cost)
                        else:
                            indices_w_cost[curr_ind+len(x)] = curr_cost+cost
            del indices_w_cost[curr_ind]
        return -1
