class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:

        C = defaultdict(lambda: float('inf'))
        for i in range(len(words)):
            word, cost = words[i], costs[i]
            C[word] = min(C[word], cost)


        @lru_cache(None)
        def dp(i):
            if i == len(target): return 0
            ans = float('inf')
            for w,c in C.items():
                if target.startswith(w,  i): ans = min(ans, c + dp(i+len(w)))
            return ans

        ans = dp(0)
        return ans if dp(0) != float('inf') else -1
