class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        // put words into trie with cost
        TrieNode trie = new TrieNode();
        for(int i=0; i<words.length; i++) {
            String word = words[i];
            TrieNode curr = trie;
            for(char c : word.toCharArray()) {
                if (curr.children[c - 'a'] == null) {
                    curr.children[c - 'a'] = new TrieNode();
                }
                curr = curr.children[c - 'a'];
            }
            if (curr.cost > costs[i]) {
                curr.word = word;
                curr.cost = costs[i];
            }
        }
        long[] dp = new long[target.length()];
        Arrays.fill(dp, -1);
        // traverse trie with target
        return (int)calcCost(trie, target, 0, dp);

    }
    public long calcCost(TrieNode trie, String s, int index, long[] dp) {
        if (index == s.length()) {
            return 0l;
        }
        if (dp[index] != -1) {
            return dp[index];
        }
        TrieNode curr = trie;
        long res = Long.MAX_VALUE;
        for(int i=index; i<s.length(); i++) {
            char c = s.charAt(i);
            if (curr.children[c - 'a'] == null) {
                break;
            }
            curr = curr.children[c - 'a'];
            if (curr.word.length() > 0) {
                long case1 = calcCost(trie, s, i + 1, dp);
                // System.out.println(case1);
                if (case1 >= 0) {
                    res = Math.min(res, curr.cost + case1);
                    // System.out.println(res);
                }
            }
        }
        return dp[index] = res == Long.MAX_VALUE ? -1 : res;
    }
}
class TrieNode {
    String word;
    int cost;
    TrieNode[] children;
    TrieNode() {
        word = "";
        cost = Integer.MAX_VALUE;
        children = new TrieNode[26];
    }
}
