#include <vector>
#include <string>
#include <climits>
#include <unordered_map>
using namespace std;

struct TrieNode {
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> wordIndices;
};

class Trie {
public:
    TrieNode* root;
    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int index, int length) {
        TrieNode* node = root;
        for (char ch : word) {
            if (node->children.find(ch) == node->children.end()) {
                node->children[ch] = new TrieNode();
            }
            node = node->children[ch];
        }
        node->wordIndices.push_back({index, length});
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        Trie trie;

        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, words[i].size());
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (node->children.find(target[j]) == node->children.end()) {
                    break;
                }
                node = node->children[target[j]];
                for (const auto& [index, length] : node->wordIndices) {
                    if (i + length <= n) {
                        dp[i + length] = min(dp[i + length], dp[i] + costs[index]);
                    }
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
