class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        Trie trie = new Trie();
        for(int i=0;i<words.length;i++){
            trie.add(words[i], costs[i]);
        }
        int[] dp = new int[target.length()];
        Arrays.fill(dp, -1);
        int ans = trie.find(target, 0, trie, dp);
        return ans == Integer.MAX_VALUE? -1: ans;
    }
}
class Trie{
    Trie[] c;
    boolean end;
    int cost;
    public Trie(){
        c = new Trie[26];
        end = false;
        cost = Integer.MAX_VALUE;
    }
    public void add(String word, int cost){
        Trie root = this;
        for(char c: word.toCharArray()){
            if(root.c[c-'a'] == null){
                root.c[c-'a'] = new Trie();
            }
            root = root.c[c-'a'];
        }
        root.end = true;
        root.cost = Math.min(cost, root.cost);
    }
    public int find(String target, int i,Trie top, int[] dp){

        if(i >= target.length()) return 0;
        if(dp[i] != -1) return dp[i];
        int cost = Integer.MAX_VALUE;
        Trie temp = top;
        for(int j=i;j<target.length();j++){
            char c = target.charAt(j);
            if(temp.c[c-'a'] != null){
                temp = temp.c[c-'a'];
                if(temp.end){
                    int sub = top.find(target, j+1,top,dp);
                    if(sub != Integer.MAX_VALUE){
                        cost = Math.min(cost, temp.cost + sub);
                    }
                }
            }else{
                if(temp.end){
                    int sub = top.find(target, j, top,dp);
                    if(sub != Integer.MAX_VALUE){
                        cost = Math.min(cost, temp.cost + sub);
                    }
                }
                break;
            }
        }
        // System.out.println("call"+i);
        //     System.out.println(cost);
        return dp[i] = cost;
    }
}
