class Solution {
public:
    #define ll long long
    struct Node {
        ll flag = 2e9;
        Node* arr[26];

        bool containsKey(int b) {
            return arr[b] != NULL;
        }

        void set(int b) {
            arr[b] = new Node();
        }

        Node* get(int b) {
            return arr[b];
        }
    };

    class Trie {
        public:
        Node* root;

        Trie() {
            root = new Node();
        }

        void insert(string &s, ll cost) {
            Node* node = root;
            for(auto el:s) {
                if(!node->containsKey(el-'a')) {
                    node->set(el-'a');
                }
                node = node->get(el-'a');
            }
            node->flag = min(node->flag, cost);
        }

        ll dfs(string &s, ll i, vector<ll> &dp) {
            if(i >= s.size()) {
                return 0;
            }
            if(dp[i] != -1) {
                return dp[i];
            }
            ll ans = 2e9;
            Node* node = root;
            for(ll j=i; j<s.size(); j++) {
                if(node->containsKey(s[j]-'a') == false) {
                    break;
                }
                node = node->get(s[j]-'a');
                if(node->flag <= 10000) {
                    ans = min(ans, node->flag + dfs(s, j+1, dp));
                }
            }

            return dp[i] = ans;
        }

        ll getAns(string target) {
            int n = target.size();
            vector<ll> dp(n+1, -1);

            return dfs(target, 0, dp);
        }

    };


    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int sz = words.size();
        Trie t;
        for(int i=0; i<sz; i++) {
            t.insert(words[i], costs[i]);
        }

        ll ans = t.getAns(target);
        if(ans >= 2e9) {
            ans = -1;
        }
        return ans;
    }
};
