class TrieNode {
public:
    TrieNode* children[26]; // Assuming lowercase English alphabet
    bool isEndOfWord;
    int cost;
    TrieNode() {
        for (int i = 0; i < 26; i++) {
            children[i] = nullptr;
        }
        isEndOfWord = false;
        cost=INT_MAX;
    }
};

class Trie {
public:
    TrieNode* root;
    Trie() {
        root = new TrieNode();
    }
    void insert(string word,int cst) {
        TrieNode* current = root;
        for (char ch : word) {
            int index = ch - 'a';
            if (!current->children[index]) {
                current->children[index] = new TrieNode();
            }
            current = current->children[index];
        }
        current->isEndOfWord = true;
        current->cost=min(current->cost,cst);
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n=target.size(),m=words.size();
        Trie *trie=new Trie();
        for(int i=0;i<m;i++) trie->insert(words[i],costs[i]);
        long long dp[n+1];
        dp[0]=0;
        for(int i=1;i<=n;i++) dp[i]=INT_MAX;
        TrieNode* rnode=trie->root;
        for(int i=0;i<n;i++){
            int index=target[i]-'a';
            if(rnode->children[index] == nullptr)  break;
            else{
                rnode=rnode->children[index];
                if(rnode->isEndOfWord) dp[i+1]=min(dp[i+1],1LL*rnode->cost);
            }
        }

        for(int i=1;i<=n;i++){
            int ans=INT_MAX;
            rnode=trie->root;
            for(int j=i;j<n;j++){
                int index=target[j]-'a';
                if(rnode->children[index] == nullptr)  break;
                else{
                    rnode=rnode->children[index];
                    if(rnode->isEndOfWord) dp[j+1]=min(dp[j+1],1LL*(rnode->cost)+dp[i]);
                }
            }
        } 
        return int(dp[n]==INT_MAX?-1:dp[n]);
    }
};
