class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {

       return minCostToFormString(target,words,costs);

    }

    private void insertWord(TrieNode root, String word, int cost) {
        TrieNode node = root;
        for (char c : word.toCharArray()) {
            node.children.putIfAbsent(c, new TrieNode());
            node = node.children.get(c);
        }
        node.cost = Math.min(node.cost, cost);
    }

    public int minCostToFormString(String target, String[] words, int[] costs) {
        TrieNode root = new TrieNode();
        for (int i = 0; i < words.length; i++) {
            insertWord(root, words[i], costs[i]);
        }

        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            TrieNode node = root;
            for (int j = i; j < n; j++) {
                if (!node.children.containsKey(target.charAt(j))) break;
                node = node.children.get(target.charAt(j));
                if (node.cost != Integer.MAX_VALUE) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}

class TrieNode {
        Map<Character, TrieNode> children;
        int cost;

        TrieNode() {
            children = new HashMap<>();
            cost = Integer.MAX_VALUE;
        }
}
