class TrieNode {
public:
    vector<TrieNode*> children;
    int minCost;

    TrieNode() : children(26, nullptr), minCost(INT_MAX) {}
};

class Solution {
private:
    void buildTrie(TrieNode* root, const vector<string>& words, const vector<int>& costs) {
        for (int i = 0; i < words.size(); i++) {
            TrieNode* node = root;
            for (char c : words[i]) {
                if (!node->children[c - 'a']) {
                    node->children[c - 'a'] = new TrieNode();
                }
                node = node->children[c - 'a'];
            }
            node->minCost = min(node->minCost, costs[i]);
        }
    }

public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        TrieNode* root = new TrieNode();
        buildTrie(root, words, costs);

        for (int i = 0; i < n; i++) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = root;
            for (int j = i; j < n; j++) {
                if (!node->children[target[j] - 'a']) break;
                node = node->children[target[j] - 'a'];
                if (node->minCost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->minCost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
