class Solution:
    def minimumCost(self, t: str, w: List[str], c: List[int]) -> int:
        dp = [float('inf')] * (len(t) + 1)
        dp[0] = 0
        dic = {}
        for i in range(len(w)):
            if w[i] not in dic: dic[w[i]] = c[i]
            dic[w[i]] = min(dic[w[i]], c[i])

        for i in range(len(t)):
            if dp[i] == float('inf'):
                    continue
            for word in dic:
                l = len(word)
                if i+l <= len(t) and t[i:i+l] == word:
                    dp[i+l] = min(dp[i+l], dp[i]+dic[word])

        return dp[len(t)] if dp[len(t)] != float('inf') else -1
