class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        Trie root = new Trie();
        for(int i = 0; i < words.length; i++) {
            String word = words[i];
            Trie tmp = root;
            for(char c : word.toCharArray()) {
                if(tmp.child[c - 'a'] == null) {
                    tmp.child[c - 'a'] = new Trie();
                }
                tmp = tmp.child[c-'a'];
            }
            tmp.cost = tmp.cost == 0 ? costs[i]: Math.min(tmp.cost, costs[i]);

        }
        Integer[] dp = new Integer[target.length() + 1];
        dp[0] = 0;
        for(int i = 1; i <= target.length(); i++) {
            if(dp[i-1] == null) continue;
            Trie tmp = root;
            int j = i - 1;
            for(; j < target.length(); j++) {
                char c= target.charAt(j);
                // System.out.println(tmp.child[c-'a']);
                if(tmp.child[c-'a'] == null) break;
                if(tmp.child[c-'a'].cost == 0) {
                    tmp = tmp.child[c-'a'];
                    continue;
                }
                if(dp[j+1] != null) {
                    dp[j+1] = Math.min(dp[j+1], dp[i-1] + tmp.child[c-'a'].cost);
                } else {
                    dp[j+1] = dp[i-1] + tmp.child[c-'a'].cost;
                }
                tmp = tmp.child[c-'a'];
            }

        }

        // for(int i = 0; i <=target.length(); i++) {
        //     System.out.println(dp[i]);
        // }

        return dp[target.length()] == null ? -1 : dp[target.length()];
    }

    class Trie {
        Trie[] child;
        Character c;
        int cost;

        Trie() {
            this.child = new Trie[26];
        }
    }
}
