class Solution {
public:
    struct TrieNode {
        map<char, TrieNode*>m;
        vector<pair<int, int>>res; 
    };

    class Trie {
    public:
        TrieNode* root;
        Trie() { root = new TrieNode(); }

        void put(string& s, int index, int cost) {
            TrieNode* node = root;
            for (int i=0;i<s.size();i++) {
                if (!node->m.count(s[i]))
                    node->m[s[i]] = new TrieNode();
                node = node->m[s[i]];
            }
            node->res.push_back({index, cost});
        }
    };

    int minimumCost(string t, vector<string> nums, vector<int> costs) {
         Trie trie;
        int n = t.size();
        vector<int> dp(n + 1, 1e9);
        dp[0] = 0;


        for (int i = 0; i < nums.size(); ++i) {
            trie.put(nums[i], i, costs[i]);
        }
int flag=1;
        for (int i=0;i<n;i++) {

            if(dp[i]==1e9) {
                flag=0;
                continue;
            }
            // for(int i=0;i<t.size();i++)if(t[i]!=nums[0][i])flag=0;

            TrieNode* node = trie.root;
            for (int j = i; j < n; j++) {
                if (node->m.find(t[j])==node->m.end()) {
                    flag=0;
                    break;
                }
                else{
                node = node->m[t[j]];
                for (auto it:node->res) {

                    if (i + nums[it.first].length() >n) {
                        flag=0;
                    }
                    else{
                       int k = nums[it.first].length();
                        int z=dp[i]+it.second;
                        int t=dp[i+k];
                        dp[i + k] = min(z,t);
                    }
                }
                }
            }
            }



        if(dp[n]!=1e9)return dp[n];
        else return -1;


    }
};
