class TrieNode {
public:
    unordered_map<char, TrieNode*> ch;
    int cost;
    TrieNode() : cost(INT_MAX) {}
};
class Trie {
    TrieNode* root;
public:
    Trie() {
        root = new TrieNode();
    }
    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (node->ch.find(c) == node->ch.end()) {
                node->ch[c] = new TrieNode();
            }
            node = node->ch[c];
        }
        node->cost = min(node->cost, cost);
    }
    TrieNode* gr() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
                    int n = target.size();
                    vector<int> dp1(n + 1, INT_MAX);
                    dp1[0] = 0;
                    Trie trie;
                    unordered_map<string, int> wm;
                                for (int i = 0; i < words.size(); ++i) {
                        if (wm.find(words[i]) == wm.end()) {
                            wm[words[i]] = costs[i];
                        } else {
                            wm[words[i]] = min(wm[words[i]], costs[i]);
                        }
                    }
            for (const auto& pair : wm) {
            trie.insert(pair.first, pair.second);
            }
            for (int i = 0; i < n; ++i) {
            if (dp1[i] == INT_MAX) continue;
            TrieNode* node = trie.gr();
            for (int j = i; j < n; ++j) {
                if (node->ch.find(target[j]) == node->ch.end()) {
                    break; 
                }
                node = node->ch[target[j]];
                if (node->cost != INT_MAX) {
                    dp1[j + 1] = min(dp1[j + 1], dp1[i] + node->cost);
                }
                }
                        }

                return dp1[n] == INT_MAX ? -1 : dp1[n];
    }
};
