class TrieNode {
public:
    vector<TrieNode*> children;
    int value;
    bool isEnd;

    TrieNode() : children(26, nullptr), value(INT_MAX), isEnd(false) {}
};

class TrieTree {
public:
    TrieNode* root;

    TrieTree() {
        root = new TrieNode();
    }

    void addWord(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children[c - 'a']) {
                node->children[c - 'a'] = new TrieNode();
            }
            node = node->children[c - 'a'];
        }
        node->value = min(cost, node->value);
        node->isEnd = true;
    }

    TrieNode* getRoot() {
        return root;
    }
};
class Solution {
public:

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {

     TrieTree trie;
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        for (int i = 0; i < words.size(); ++i) {
            trie.addWord(words[i], costs[i]);
        }
        dp[0] = 0;


        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = trie.getRoot();
            for (int j = i; j < n; ++j) {
                char c = target[j];
                if (!node->children[c - 'a']) break;
                node = node->children[c - 'a'];
                if (node->isEnd) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->value);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];

    }
};
