class Solution {
public:
     struct trie{
        trie* child[26];
        string s="";
        int cost=0;
        vector<int>v;
    };
    vector<string>ans;
    trie* root;
    void insert(string key,int i,int c,trie* root){
        for(int i=0;i<key.size();i++){
            if(root->child[key[i]-'a']==NULL){
                root->child[key[i]-'a']=new trie();
            }
            root=root->child[key[i]-'a'];
        }
        root->s=key;
        root->v.push_back(i);
    }
    int helper1(int i,string target,trie* curr,trie* newnode){
         if(i==target.size()) return 0;
         if(curr->s!="" && newnode->s!=""){
            ans.push_back(curr->s);
            curr->s="";
         }
         if(newnode->s!=""){
            return helper1(i,target,curr,root);
         }
         int ans=0;
         if(curr->child[i]!=NULL && newnode->child[i]!=NULL){
            ans=min(ans,helper1(i+1,target,curr->child[i],newnode->child[i]));
        }
        return ans;
    }
    int helper(int i,string target,trie* curr){
        if(curr->s!=""){
            return helper1(i,target,curr,root);
        }
        return 0;
    }
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n=words.size();
        root=new trie();
        for(int i=0;i<n;i++){
            insert(words[i],i,costs[i],root);
        }
        n=target.size();
        vector<int>dp(n+5,1e9);
        dp[0]=0;
        for(int i=0;i<n;i++){
            if(dp[i]==1e9) continue;
            trie* curr=root;
            for(int j=i;j<n;j++){
                if(curr->child[target[j]-'a']==NULL) break;
                curr=curr->child[target[j]-'a'];
                if(curr->s=="") continue;
                for(int x:curr->v){
                    int z=words[x].size();
                    if(i+z<=n){
                        dp[i+z]=min(dp[i+z],dp[i]+costs[x]);
                    }
                }
            }
        }
        if(dp[n]==1e9) return -1;
        return dp[n];
    }
};
