class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        m = len(words)
        a = {}
        for i,w in enumerate(words):
            a[w] = min(a.get(w,float('inf')),costs[i])
        ans = [float('inf')]*(n+1)
        ans[0] = 0

        for i in range(1,n+1):
            for w in a:
                if i >= len(w) and target[i-len(w):i]== w:
                    ans[i] = min(ans[i],ans[i-len(w)]+ a[w])

        return ans[n] if ans[n] != float('inf') else -1
