class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        Trie trie = new Trie();
        for(int i=0;i<words.length;i++)
        {
            Trie curr = trie;
            for(int j=0;j<words[i].length();j++)
            {
                int ind = (int)(words[i].charAt(j)-'a');
                if(curr.arr[ind] == null)
                    curr.arr[ind] = new Trie();
                curr = curr.arr[ind];
            }
            curr.currVal = Math.min(curr.currVal, costs[i]);
        }
        char[] nums = target.toCharArray();
        int[] dp = new int[target.length()];
        Arrays.fill(dp,-1);
        int ans = check(0,dp,trie,nums);
        return ans >= (int)1e9? -1 : ans;
    }

    private int check(int curr, int[] dp, Trie trie, char[] nums)
    {
        int n = dp.length;
        if(curr == n)
            return 0;
        if(dp[curr]!= -1)
            return dp[curr];
        int min = (int)1e9;
        Trie currTrie = trie;
        for(int i=curr;i<n;i++)
        {
            int ind = (int)(nums[i]-'a');
            if(currTrie.arr[ind] == null)
                break;
            currTrie = currTrie.arr[ind];
            if(currTrie.currVal<(int)1e9)
            {
                min = Math.min(min,currTrie.currVal+check(i+1,dp,trie,nums));
            }
        }
        return dp[curr] = min;
    }
}

class Trie{
    int currVal;
    Trie[] arr;
    Trie()
    {
        currVal = (int)1e9;
        arr = new Trie[26];
    }
}
