class TrieVertex {
public:
    vector<TrieVertex*> children;
    int value;
    bool isEndOfWord;

    TrieVertex() : children(26, nullptr), value(INT_MAX), isEndOfWord(false) {}
};

class TrieTree {
public:
    TrieVertex* root;

    TrieTree() {
        root = new TrieVertex();
    }

    void addWord(const string& word, int cost) {
        TrieVertex* node = root;
        for (char c : word) {
            if (!node->children[c - 'a']) {
                node->children[c - 'a'] = new TrieVertex();
            }
            node = node->children[c - 'a'];
        }
        node->value = min(cost, node->value);
        node->isEndOfWord = true;
    }

    TrieVertex* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        TrieTree trie;
        int length = target.size();
        vector<int> minimumCost(length + 1, INT_MAX);

        for (int i = 0; i < words.size(); ++i) {
            trie.addWord(words[i], costs[i]);
        }

        minimumCost[0] = 0;

        for (int i = 0; i < length; ++i) {
            if (minimumCost[i] == INT_MAX) continue;

            TrieVertex* node = trie.getRoot();
            for (int j = i; j < length; ++j) {
                char c = target[j];
                if (!node->children[c - 'a']) break;
                node = node->children[c - 'a'];
                if (node->isEndOfWord) {
                    minimumCost[j + 1] = min(minimumCost[j + 1], minimumCost[i] + node->value);
                }
            }
        }

        return minimumCost[length] == INT_MAX ? -1 : minimumCost[length];
    }
};
