class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)

    def search(self, s, start):
        node = self.root
        matches = []
        for i in range(start, len(s)):
            if s[i] not in node.children:
                break
            node = node.children[s[i]]
            if node.cost != float('inf'):
                matches.append((i + 1, node.cost))
        return matches
class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        m = len(words)

        # Build the Trie
        trie = Trie()
        for i in range(m):
            trie.insert(words[i], costs[i])

        # Initialize dp array with infinity
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # Fill the dp array using the Trie
        for i in range(n):
            if dp[i] == float('inf'):
                continue
            matches = trie.search(target, i)
            for end, cost in matches:
                dp[end] = min(dp[end], dp[i] + cost)

        return dp[n] if dp[n] != float('inf') else -1
