class TrieNode{
    public:
    TrieNode * children[26];
    bool IsEnd;
    int cost;
    TrieNode(){
        for(int i=0;i<26;i++)children[i]=NULL;
        IsEnd=false;
        cost=0;
    }
};
class Trie{
    public:
    TrieNode *root;
	int n;
    Trie(){
       root=new TrieNode;
    }

    void insert(string &word,int cost){
         TrieNode * node=root;
        int size=word.size();
        for(int i=0;i<size;i++){
            int t=word[i]-'a';
			// cout<<word[i]<<' ';
            if(node->children[t]==nullptr){
                TrieNode * ins=new TrieNode;
                node->children[t]=ins;
            }
            node=node->children[t];
        }
		// cout<<endl;
		if(node->IsEnd) node->cost=min(node->cost,cost);
		else{
        node->IsEnd=true;
		node->cost=cost;

		}

        return;
    }

    long long int countWordsEqualTo(string &word,int indx,vector<lli> &dp){
		if(indx==n){
			return 0;
		}
		if(indx>n){
			return 1e12;
		}
		if(dp[indx]!=-1) return dp[indx];
        TrieNode * node=root;
		lli ans=1e12;
        for(int i=indx;i<n;i++){
            int t=word[i]-'a';
            if(node->children[t]==nullptr) {
				// cout<<i<<' ';
				break;

			}
            node=node->children[t];
			if(node->IsEnd){
				// cout<<i<<' ';
				ans=min(ans,node->cost+countWordsEqualTo(word,i+1,dp));
			}
        }
        return dp[indx]=ans;
    } 
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie T;
		int size=words.size();
		for(int i=0;i<size;i++){
			// cout<<words[i]<<' ';
			T.insert(words[i],costs[i]);
		}
		// cout<<endl;
		T.n=target.size();
		vector<lli> dp(T.n,-1);
		lli ans=T.countWordsEqualTo(target,0,dp);

		if(ans>=1e12) return -1;
		return ans;

    }
};
