class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        m=len(words)
        wc={}
        for i,j in enumerate(words):
            wc[j]=min(wc.get(j,float('inf')),costs[i])
        dp = [float('inf')] * (n + 1)
        dp[0] = 0  
        for i in range(1,n+1):  
            for j in wc:
                if i>=len(j) and target[i-len(j):i]==j:
                    dp[i] = min(dp[i],dp[i-len(j)]+wc[j])
        return dp[n] if dp[n] != float('inf') else -1
