class T {

public:

    vector<T*> c;

    int v;

    bool e;

    T() : c(26, nullptr), v(INT_MAX), e(false) {}

};

class R {

public:

    T* r;

    R() {

        r = new T();

    }

    void a(const string& w, int x) {

        T* n = r;

        for (char h : w) {

            if (!n->c[h - 'a']) {

                n->c[h - 'a'] = new T();

            }

            n = n->c[h - 'a'];

        }

        n->v = min(x, n->v);

        n->e = true;

    }

    T* g() {

        return r;

    }

};


    int f(string t, vector<string>& w, vector<int>& c) {

        R r;

        int l = t.size();

        vector<int> m(l + 1, INT_MAX);

        for (int i = 0; i < w.size(); ++i) {

            r.a(w[i], c[i]);

        }

        m[0] = 0;

        for (int i = 0; i < l; ++i) {

            if (m[i] == INT_MAX) continue;

            T* n = r.g();

            for (int j = i; j < l; ++j) {

                char h = t[j];

                if (!n->c[h - 'a']) break;

                n = n->c[h - 'a'];

                if (n->e) {

                    m[j + 1] = min(m[j + 1], m[i] + n->v);

                }

            }

        }

        return m[l] == INT_MAX ? -1 : m[l];

    }



class Solution {
public:
    int minimumCost(string t, vector<string>& w, vector<int>& c){

         // S obj = new S();


       return f(t, w,c);     

    }
};
