class Solution {
    class Node {
            char c = '\0';
            int cost = -1;

            public Node() {

            }

            public Node(char c, int cost) {
                this.c = c;
                this.cost = cost;
            }

            Node[] child = new Node[26];
        }

    class Trie {



        Node head = new Node();

        public void add(String word, int cost) {
            Node cur = head;
            for (int i = 0; i < word.length(); i++) {
                if (cur.child[word.charAt(i) - 'a'] == null) {
                    cur.child[word.charAt(i) - 'a'] = new Node(word.charAt(i), -1);
                }

                cur = cur.child[word.charAt(i) - 'a'];
            }

            cur.cost = cur.cost == -1 ? cost : Math.min(cur.cost, cost);
        }
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        Trie trie = new Trie();
        for (int i = 0; i < words.length; i++) {
            trie.add(words[i], costs[i]);
        }

        int[] cost = new int[target.length()+1];
        for (int i = 1; i < cost.length; i++) {
            cost[i] = Integer.MAX_VALUE;
        }

        for (int i = 0; i < cost.length-1; i++) {
            if (cost[i] == Integer.MAX_VALUE) {
                continue;
            }

            Node cur = trie.head;
            for (int length = 0; i + length < cost.length-1; length++) {
                cur = cur.child[target.charAt(i + length) - 'a'];
                if (cur == null) {
                    break;
                }

                if (cur.cost == -1) {
                    continue;
                }

                cost[i+length+1] = Math.min(cost[i+length+1], cost[i] + cur.cost);
            }
        }

        return cost[cost.length-1] == Integer.MAX_VALUE ? -1 : cost[cost.length-1];
    }
}
