class Solution {
public:
    class Node{
    Node* a[26]={};
    bool wordEnd=false;
    public:
        int cost=1e9;
        Node(){    
        }
        bool doesExist(int idx){
            return a[idx]!=NULL;
        }
        void put(int idx,Node*temp){
            a[idx]=temp;
        }
        Node*get(int idx){
            return a[idx];
        }
        void wordEnded(){
            wordEnd=true;
        }
        bool isWordEnd(){
            return wordEnd==true;
        }
    };
    class Trie{
        Node*root;
    public:
        Trie(){
            root=new Node();
        }
        void insert(string &s,int c){
            Node*temp=root;
            int n=s.size();
            for(int i=0;i<n;i++){
                int chIdx=s[i]-'a';
                if(!temp->doesExist(chIdx))
                    temp->put(chIdx,new Node());
                temp=temp->get(chIdx);
            }
            temp->cost=min(temp->cost,c);
            temp->wordEnded();
        }
        int getMinCost(int idx,string &t,int &n,vector<int>&dp){
            if(idx==n)return 0;
            if(dp[idx]!=-1)return dp[idx];
            int ans=1e9;
            Node*temp=root;
            int i;
            for(i=idx;i<n;i++){
                int chIdx=t[i]-'a';
                // cout<<i<<" "<<temp->cost<<endl;
                if(temp->isWordEnd())ans=min(ans,temp->cost+getMinCost(i,t,n,dp));
                if(temp->doesExist(chIdx))temp=temp->get(chIdx);
                else break;
            }
            // cout<<idx<<"-> "<<temp->cost<<endl;
            if(temp->isWordEnd() && i==n)return dp[idx]=min(ans,temp->cost);
            return dp[idx]=ans;
        }

    };
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n=target.size();
        int m=words.size();
        Trie *t=new Trie();
        vector<int>dp(n,-1);
        for(int i=0;i<m;i++)
            t->insert(words[i],costs[i]);
        int val=t->getMinCost(0,target,n,dp);
        if(val>=1e9)return -1;
        return val;
    }
};
