class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)

        trie = {}

        for i, w in enumerate(words):
            node = trie
            for c in w:
                if c not in node:
                    node[c] = {}
                node = node[c]
            if '$' in node:
                node['$'] = min(node['$'], costs[i])
            else:
                node['$'] = costs[i]

        # print(trie)
        @cache
        def tryNext(i):
            if i == n:
                return 0

            score = float('inf')
            node = trie
            for j in range(i, n):
                if target[j] not in node:
                    break

                node = node[target[j]]
                # print(target[j], node)
                if '$' in node:
                    score = min(score, node['$'] + tryNext(j + 1))

            return score

        res = tryNext(0)

        if res == float('inf'):
            return -1

        return res
