class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)

    def search(self, target, start):
        node = self.root
        results = []
        for i in range(start, len(target)):
            if target[i] in node.children:
                node = node.children[target[i]]
                if node.cost != float('inf'):
                    results.append((i + 1, node.cost))
            else:
                break
        return results

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        trie = Trie()

        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        for i in range(n):
            if dp[i] == float('inf'):
                continue
            matches = trie.search(target, i)
            for next_pos, cost in matches:
                if next_pos <= n:
                    dp[next_pos] = min(dp[next_pos], dp[i] + cost)

        return dp[n] if dp[n] != float('inf') else -1
