struct TrieNode {
    unordered_map<char, TrieNode*> children;
    int minCost;

    TrieNode() : minCost(INT_MAX) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string word, int cost) {
        TrieNode* node = root;
        for (char ch : word) {
            if (!node->children.count(ch)) {
                node->children[ch] = new TrieNode();
            }
            node = node->children[ch];
        }
        node->minCost = min(node->minCost, cost);
    }

    int search(string prefix) {
        TrieNode* node = root;
        for (char ch : prefix) {
            if (!node->children.count(ch)) {
                return INT_MAX;
            }
            node = node->children[ch];
        }
        return node->minCost;
    }
};

class Solution {
public:
    int minimumCost(string t, vector<string>& words, vector<int>& costs) {
        Trie trie;
        int n = t.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < words.size(); i++) {
            trie.insert(words[i], costs[i]);
        }

        for (int i = 0; i < n; i++) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = trie.root;
            for (int j = i; j < n; j++) {
                if (!node->children.count(t[j])) break;
                node = node->children[t[j]];
                if (node->minCost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->minCost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
