class Trie {
    char current = '*';
    String currentWord = null;
    int wordCost = -1;
    Trie[] children = new Trie[26];

    Trie(char current) {
        this.current = current;
    }
}

class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        Trie root = new Trie('*');

        for (int i = 0;i < words.length;i++) {
            String word = words[i];
            Trie currentNode = root;
            for (char letter : word.toCharArray()) {
                if (currentNode.children[letter - 'a'] == null) {
                    currentNode.children[letter - 'a'] = new Trie(letter);
                }
                currentNode = currentNode.children[letter - 'a'];
            }
            currentNode.currentWord = word;
            if (currentNode.wordCost == -1) {
                currentNode.wordCost = costs[i];
            } else {
                currentNode.wordCost = Math.min(currentNode.wordCost, costs[i]);
            }

        }

        int[] costAtIndex = new int[target.length()];
        Arrays.fill(costAtIndex, Integer.MAX_VALUE);

        for (int i = 0;i < target.length();i++) {
            Trie currentNode = root;
            int uptoCost = 0;
            if (i != 0) {
                if (costAtIndex[i - 1] == Integer.MAX_VALUE) {
                    continue;
                }
                uptoCost = costAtIndex[i - 1];
            }
            for (int j = i;j < target.length();j++) {
                if (currentNode.children[target.charAt(j) - 'a'] == null) {
                    break;
                }
                currentNode = currentNode.children[target.charAt(j) - 'a'];
                if (currentNode.wordCost != -1) {
                    costAtIndex[j] = Math.min(costAtIndex[j], uptoCost + currentNode.wordCost);
                }
            }
        }
        return costAtIndex[costAtIndex.length - 1] == Integer.MAX_VALUE ? -1 : costAtIndex[costAtIndex.length - 1];
    }
}
